    private void btnCalcularBolsaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularBolsaActionPerformed
        // TODO add your handling code here:
        
        Integer qtdFilhos1 = Integer.valueOf(txFilhos2.getText());
        Integer qtdFilhos2 = Integer.valueOf(txFilhos7.getText());
        Integer qtdFilhos3 = Integer.valueOf(txFilhos15.getText());
        Double total = (qtdFilhos1 * 15.5) + (qtdFilhos2 * 10) + (qtdFilhos3 * 5.5);
        
        lblresult.setText(String.format("Sua bolsa kids ser√° de R$%.2f", total));
        

    }//GEN-LAST:event_btnCalcularBolsaActionPerformed

